export default function Producto() {
  const tieneDescuento = false;
  const stock = 3;
  const precio = 2000;

  return (
    <div>
      {
        tieneDescuento
          ? <p>S/ {precio - precio * 0.20} con descuento</p>
          : <p>Sin descuento: S/ {precio}</p>
      }

      {
        stock < 5 && <p>¡Solo quedan {stock} unidades!</p>
      }
    </div>
  );
}
