import { render, screen } from '@testing-library/react';
import '@testing-library/jest-dom';
import CatalogoProductos from './App';

test('renderiza el título principal', () => {
  render(<CatalogoProductos />);
  expect(screen.getByText(/mi tienda online/i)).toBeInTheDocument();
});

test('muestra al menos 4 productos', () => {
  render(<CatalogoProductos />);
  const nombres = screen.getAllByRole('heading', { level: 3 });
  expect(nombres.length).toBeGreaterThanOrEqual(4);
});

test('muestra el precio de cada producto con símbolo $', () => {
  render(<CatalogoProductos />);
  const precios = screen.getAllByText(/\$/i);
  expect(precios.length).toBeGreaterThanOrEqual(4);
});

test('muestra estado de disponibilidad', () => {
  render(<CatalogoProductos />);
  const disponibles = screen.queryAllByText(/disponible/i);
  const agotados = screen.queryAllByText(/agotado/i);
  expect(disponibles.length + agotados.length).toBeGreaterThanOrEqual(4);
});

test('muestra alerta de stock bajo', () => {
  render(<CatalogoProductos />);
  const alertas = screen.queryAllByText(/solo quedan/i);
  expect(alertas.length).toBeGreaterThan(0);
});

test('filtra productos de PlayStation', () => {
  render(<CatalogoProductos />);
  expect(screen.getByText(/juegos de playstation/i)).toBeInTheDocument();
});

test('usa key única en elementos de la lista', () => {
  const code = CatalogoProductos.toString();
  expect(code).toMatch(/key=/);
});
