import { render, screen, fireEvent } from '@testing-library/react';
import '@testing-library/jest-dom';
import TaskManager from './App';

test('renderiza el título principal', () => {
  render(<TaskManager />);
  expect(screen.getByText(/mi lista de tareas/i)).toBeInTheDocument();
});

test('renderiza las tareas iniciales', () => {
  render(<TaskManager />);
  expect(screen.getByText(/aprender react/i)).toBeInTheDocument();
  expect(screen.getByText(/practicar useState/i)).toBeInTheDocument();
});

test('agregar una nueva tarea', () => {
  render(<TaskManager />);
  const input = screen.getByPlaceholderText(/nueva tarea/i);
  const button = screen.getByText(/agregar tarea/i);

  fireEvent.change(input, { target: { value: 'Nueva tarea de prueba' } });
  fireEvent.click(button);

  expect(screen.getByText(/nueva tarea de prueba/i)).toBeInTheDocument();
});

test('marcar tarea como completada aplica line-through', () => {
  render(<TaskManager />);
  const checkboxes = screen.getAllByRole('checkbox');

  fireEvent.click(checkboxes[0]);

  const taskSpans = screen.getAllByText(/aprender react/i);
  const mainTaskSpan = taskSpans.find(el => el.tagName === 'SPAN');
  expect(mainTaskSpan).toHaveStyle('text-decoration: line-through');
});

test('tareas completadas aparecen en sección separada', () => {
  render(<TaskManager />);
  const checkboxes = screen.getAllByRole('checkbox');

  fireEvent.click(checkboxes[0]);

  const completedSection = screen.getByText(/tareas completadas/i);
  expect(completedSection).toBeInTheDocument();

  const allMatches = screen.getAllByText(/aprender react/i);
  expect(allMatches.length).toBeGreaterThanOrEqual(2);
});

test('cada tarea tiene una key única', () => {
  const originalError = console.error;
  const errors = [];
  console.error = (...args) => {
    errors.push(args);
  };

  render(<TaskManager />);

  console.error = originalError;

  const hasKeyWarning = errors.some(error => 
    error.some(arg => 
      typeof arg === 'string' && (arg.includes('unique "key" prop') || arg.includes('Each child in a list'))
    )
  );

  expect(hasKeyWarning).toBe(false);
});

test('input se limpia después de agregar tarea', () => {
  render(<TaskManager />);
  const input = screen.getByPlaceholderText(/nueva tarea/i);
  const button = screen.getByText(/agregar tarea/i);

  fireEvent.change(input, { target: { value: 'Tarea temporal' } });
  fireEvent.click(button);

  expect(input.value).toBe('');
});
