import { render, screen, fireEvent } from '@testing-library/react';
import '@testing-library/jest-dom';
import RegistrationForm from './App';

test('renderiza el título principal', () => {
  render(<RegistrationForm />);
  expect(screen.getByText(/formulario de registro/i)).toBeInTheDocument();
});

test('renderiza todos los campos del formulario', () => {
  render(<RegistrationForm />);
  expect(screen.getByLabelText(/nombre/i)).toBeInTheDocument();
  expect(screen.getByLabelText(/email/i)).toBeInTheDocument();
  expect(screen.getByLabelText(/contraseña/i)).toBeInTheDocument();
  expect(screen.getByLabelText(/país/i)).toBeInTheDocument();
  expect(screen.getByText(/intereses/i)).toBeInTheDocument();
  expect(screen.getByText(/nivel de experiencia/i)).toBeInTheDocument();
  expect(screen.getByLabelText(/biografía/i)).toBeInTheDocument();
});

test('valida nombre con menos de 3 caracteres', () => {
  render(<RegistrationForm />);
  const input = screen.getByLabelText(/nombre/i);

  fireEvent.change(input, { target: { value: 'Ab' } });

  expect(screen.getByText(/el nombre debe tener al menos 3 caracteres/i)).toBeInTheDocument();
});

test('valida email inválido', () => {
  render(<RegistrationForm />);
  const input = screen.getByLabelText(/email/i);

  fireEvent.change(input, { target: { value: 'correosinvalido' } });

  expect(screen.getByText(/email inválido/i)).toBeInTheDocument();
});

test('valida contraseña con menos de 6 caracteres', () => {
  render(<RegistrationForm />);
  const input = screen.getByLabelText(/contraseña/i);

  fireEvent.change(input, { target: { value: '12345' } });

  expect(screen.getByText(/la contraseña debe tener al menos 6 caracteres/i)).toBeInTheDocument();
});

test('permite seleccionar un país', () => {
  render(<RegistrationForm />);
  const select = screen.getByLabelText(/país/i);

  fireEvent.change(select, { target: { value: 'peru' } });

  expect(select.value).toBe('peru');
});

test('permite seleccionar múltiples intereses con checkboxes', () => {
  render(<RegistrationForm />);
  const checkboxes = screen.getAllByRole('checkbox');

  fireEvent.click(checkboxes[0]);
  fireEvent.click(checkboxes[1]);

  expect(checkboxes[0]).toBeChecked();
  expect(checkboxes[1]).toBeChecked();
});

test('permite seleccionar nivel con radio buttons', () => {
  render(<RegistrationForm />);
  const radios = screen.getAllByRole('radio');

  fireEvent.click(radios[1]);

  expect(radios[1]).toBeChecked();
  expect(radios[0]).not.toBeChecked();
});

test('muestra contador de caracteres en biografía', () => {
  render(<RegistrationForm />);
  const textarea = screen.getByLabelText(/biografía/i);

  fireEvent.change(textarea, { target: { value: 'Hola mundo' } });

  expect(screen.getByText(/10\/200 caracteres/i)).toBeInTheDocument();
});

test('deshabilita el botón cuando hay errores de validación', () => {
  render(<RegistrationForm />);
  const button = screen.getByRole('button', { name: /registrarse/i });
  const nombreInput = screen.getByLabelText(/nombre/i);

  fireEvent.change(nombreInput, { target: { value: 'Ab' } });

  expect(button).toBeDisabled();
});

test('habilita el botón cuando el formulario es válido', () => {
  render(<RegistrationForm />);
  const button = screen.getByRole('button', { name: /registrarse/i });

  fireEvent.change(screen.getByLabelText(/nombre/i), { target: { value: 'Juan Pérez' } });
  fireEvent.change(screen.getByLabelText(/email/i), { target: { value: 'juan@example.com' } });
  fireEvent.change(screen.getByLabelText(/contraseña/i), { target: { value: '123456' } });
  fireEvent.change(screen.getByLabelText(/país/i), { target: { value: 'peru' } });
  fireEvent.click(screen.getAllByRole('checkbox')[0]);
  fireEvent.click(screen.getAllByRole('radio')[0]);

  expect(button).not.toBeDisabled();
});

test('los mensajes de error se muestran en rojo', () => {
  render(<RegistrationForm />);
  const input = screen.getByLabelText(/nombre/i);

  fireEvent.change(input, { target: { value: 'Ab' } });

  const errorMsg = screen.getByText(/el nombre debe tener al menos 3 caracteres/i);
  expect(errorMsg).toHaveStyle('color: red');
});
