export default function App() {
  const users = [
    { id: 1, name: 'Carlos', age: 22 },
    { id: 2, name: 'Ana', age: 28 },
    { id: 3, name: 'Beto', age: 19 },
    { id: 4, name: 'Daniela', age: 30 },
  ]

  const filtered = users
    .filter((u) => u.age >= 21)
    .sort((a, b) => a.name.localeCompare(b.name))

  return (
    <ul>
      {filtered.map((user) => (
        <li key={user.id}>
          {user.name} - {user.age}
        </li>
      ))}
    </ul>
  )
}
