import { render, screen, fireEvent } from '@testing-library/react';
import '@testing-library/jest-dom';
import App from './App';

test('renderiza título principal', () => {
  render(<App />);
  expect(screen.getByText(/challenge: renderizado en react/i)).toBeInTheDocument();
});

test('muestra mensaje de login correcto', () => {
  render(<App />);
  expect(screen.getByText(/por favor inicia sesión/i)).toBeInTheDocument();
  fireEvent.click(screen.getByText(/toggle login/i));
  expect(screen.getByText(/bienvenido, usuario/i)).toBeInTheDocument();
});

test('filtra usuarios correctamente', () => {
  render(<App />);
  const input = screen.getByPlaceholderText(/filtrar usuarios/i);
  fireEvent.change(input, { target: { value: 'Mar' } });
  expect(screen.getByText(/maría - 30/i)).toBeInTheDocument();
  expect(screen.queryByText(/juan - 25/i)).toBeNull();
});

test('ordena usuarios por edad', () => {
  render(<App />);
  const button = screen.getByText(/ordenar por edad/i);
  fireEvent.click(button);
  const items = screen.getAllByRole('listitem');
  expect(items[0].textContent).toMatch(/pedro - 20/i);
  expect(items[items.length - 1].textContent).toMatch(/maría - 30/i);
});

test('hace click en usuario llama la función', () => {
  const consoleSpy = jest.spyOn(console, 'log');
  render(<App />);
  fireEvent.click(screen.getByText(/juan - 25/i));
  expect(consoleSpy).toHaveBeenCalledWith('Usuario clickeado', 1);
  consoleSpy.mockRestore();
});
