import { useState, useEffect } from 'react'

export default function Contador() {
  const [contador, setContador] = useState(0);

  useEffect(() => {
    console.log('El efecto se ejecutó. Contador:', contador);
  });

  return (
    <button onClick={() => setContador(contador + 1)}>
      Clicks: {contador}
    </button>
  );
}
