import { useState, useEffect } from 'react'

export default function App() {
  const [nombre, setNombre] = useState('Carlos');
  const [mensaje, setMensaje] = useState('');

  useEffect(() => {
    console.log('Nombre cambiado:', nombre);
  }, [nombre]);

  useEffect(() => {
    setMensaje('Componente montado');
  }, []);

  return <p>{mensaje}</p>;
}
