import { render, screen, fireEvent } from '@testing-library/react';
import '@testing-library/jest-dom';
import UserProfile from './App';

test('muestra el nombre del usuario', () => {
  render(<UserProfile />);
  const heading = screen.getByRole('heading', { level: 1 });
  expect(heading).toBeInTheDocument();
});

test('permite editar el nombre del usuario', () => {
  render(<UserProfile />);
  const input = screen.getByRole('textbox');

  fireEvent.change(input, { target: { value: 'Juan Pérez' } });
  expect(input.value).toBe('Juan Pérez');
});

test('muestra el botón de guardar', () => {
  render(<UserProfile />);
  const button = screen.getByRole('button', { name: /guardar/i });
  expect(button).toBeInTheDocument();
});

test('actualiza el nombre al hacer clic en guardar', () => {
  render(<UserProfile />);
  const input = screen.getByRole('textbox');
  const button = screen.getByRole('button', { name: /guardar/i });

  fireEvent.change(input, { target: { value: 'María García' } });
  fireEvent.click(button);

  expect(screen.getByText(/maría garcía/i)).toBeInTheDocument();
});

test('usa useState correctamente', () => {
  const code = UserProfile.toString();
  expect(code).toMatch(/useState/);
});
