import { render, screen, fireEvent } from '@testing-library/react';
import '@testing-library/jest-dom';
import ContadorAvanzado from './App';

test('muestra el título', () => {
  render(<ContadorAvanzado />);
  const heading = screen.getByRole('heading', { level: 1 });
  expect(heading).toBeInTheDocument();
});

test('muestra el contador inicial en 0', () => {
  render(<ContadorAvanzado />);
  expect(screen.getByText(/contador: 0/i)).toBeInTheDocument();
});

test('tiene botón incrementar', () => {
  render(<ContadorAvanzado />);
  const button = screen.getByRole('button', { name: /incrementar/i });
  expect(button).toBeInTheDocument();
});

test('tiene botón decrementar', () => {
  render(<ContadorAvanzado />);
  const button = screen.getByRole('button', { name: /decrementar/i });
  expect(button).toBeInTheDocument();
});

test('tiene botón reset', () => {
  render(<ContadorAvanzado />);
  const button = screen.getByRole('button', { name: /reset/i });
  expect(button).toBeInTheDocument();
});

test('tiene input para salto personalizado', () => {
  render(<ContadorAvanzado />);
  const input = screen.getByRole('spinbutton');
  expect(input).toBeInTheDocument();
});

test('incrementa el contador', () => {
  render(<ContadorAvanzado />);
  const button = screen.getByRole('button', { name: /incrementar/i });

  fireEvent.click(button);
  expect(screen.getByText(/contador: 1/i)).toBeInTheDocument();
});

test('decrementa el contador', () => {
  render(<ContadorAvanzado />);
  const incrementBtn = screen.getByRole('button', { name: /incrementar/i });
  const decrementBtn = screen.getByRole('button', { name: /decrementar/i });

  fireEvent.click(incrementBtn);
  fireEvent.click(incrementBtn);
  fireEvent.click(decrementBtn);

  expect(screen.getByText(/contador: 1/i)).toBeInTheDocument();
});

test('resetea el contador a 0', () => {
  render(<ContadorAvanzado />);
  const incrementBtn = screen.getByRole('button', { name: /incrementar/i });
  const resetBtn = screen.getByRole('button', { name: /reset/i });

  fireEvent.click(incrementBtn);
  fireEvent.click(incrementBtn);
  fireEvent.click(resetBtn);

  expect(screen.getByText(/contador: 0/i)).toBeInTheDocument();
});

test('enfoca el input al hacer clic en enfocar', () => {
  render(<ContadorAvanzado />);
  const input = screen.getByRole('spinbutton');
  const focusBtn = screen.getByRole('button', { name: /enfocar input/i });

  fireEvent.click(focusBtn);
  expect(input).toHaveFocus();
});

test('usa useReducer correctamente', () => {
  const code = ContadorAvanzado.toString();
  expect(code).toMatch(/useReducer/);
});

test('usa useRef correctamente', () => {
  const code = ContadorAvanzado.toString();
  expect(code).toMatch(/useRef/);
});
