import React from 'react'

function Tarjeta({ children }) {
  return (
    <div
      style={{
        border: '2px solid #e2e8f0',
        borderRadius: '8px',
        padding: '20px',
        marginBottom: '20px',
        backgroundColor: '#ffffff'
      }}
    >
      {children}
    </div>
  )
}

function Alerta({ children, tipo = 'info' }) {
  const colores = {
    info: '#dbeafe',
    exito: '#dcfce7',
    error: '#fee2e2'
  }

  return (
    <div
      style={{
        backgroundColor: colores[tipo],
        padding: '15px',
        borderRadius: '8px',
        marginBottom: '15px'
      }}
    >
      {children}
    </div>
  )
}

export default function App() {
  return (
    <div style={{ padding: '20px' }}>
      <Tarjeta>
        <h2>Bienvenido</h2>
        <p>Este contenido está dentro de Tarjeta.</p>
      </Tarjeta>

      <Alerta tipo='info'>Mensaje informativo</Alerta>
      <Alerta tipo='exito'>Operación realizada con éxito</Alerta>
      <Alerta tipo='error'>Ha ocurrido un error</Alerta>
    </div>
  )
}
