import { render, screen } from '@testing-library/react';
import '@testing-library/jest-dom';
import PerfilUsuario from './App';

test('muestra el título principal', () => {
  render(<PerfilUsuario />);
  const heading = screen.getByRole('heading', { level: 1 });
  expect(heading).toBeInTheDocument();
});

test('muestra el nombre del usuario', () => {
  render(<PerfilUsuario />);
  expect(screen.getByText(/juan pérez/i)).toBeInTheDocument();
});

test('muestra el email del usuario', () => {
  render(<PerfilUsuario />);
  expect(screen.getByText(/juan@example.com/i)).toBeInTheDocument();
});

test('muestra la edad del usuario', () => {
  render(<PerfilUsuario />);
  expect(screen.getByText(/25 años/i)).toBeInTheDocument();
});

test('tiene al menos dos componentes Card', () => {
  const code = PerfilUsuario.toString();
  const cardMatches = code.match(/<Card/g);
  expect(cardMatches).not.toBeNull();
  expect(cardMatches.length).toBeGreaterThanOrEqual(2);
});

test('el componente Card usa children', () => {
  const code = PerfilUsuario.toString();
  expect(code).toMatch(/function Card.*children/);
});

test('muestra información en formato de tarjeta', () => {
  const { container } = render(<PerfilUsuario />);
  const cards = container.querySelectorAll('[style*="border"]');
  expect(cards.length).toBeGreaterThanOrEqual(2);
});

test('tiene título Información Personal', () => {
  render(<PerfilUsuario />);
  expect(screen.getByText(/información personal/i)).toBeInTheDocument();
});

test('tiene título Contacto', () => {
  render(<PerfilUsuario />);
  expect(screen.getByText(/contacto/i)).toBeInTheDocument();
});
