import { render, screen } from '@testing-library/react';
import '@testing-library/jest-dom';
import PerfilUsuario from './App';

test('muestra el título principal', () => {
  render(<PerfilUsuario />);
  expect(screen.getByText(/perfil de usuario/i)).toBeInTheDocument();
});

test('muestra el nombre del usuario', () => {
  render(<PerfilUsuario />);
  expect(screen.getByText(/juan pérez/i)).toBeInTheDocument();
});

test('muestra el email del usuario', () => {
  render(<PerfilUsuario />);
  expect(screen.getByText(/juan@example.com/i)).toBeInTheDocument();
});

test('muestra la edad del usuario', () => {
  render(<PerfilUsuario />);
  expect(screen.getByText(/25 años/i)).toBeInTheDocument();
});

